var Piece = require('./Piece');

// Now, this one will call the "superclass"'s constructor and apply it to itself
function king(color) {
    Piece.call(this);
    this.name = 'K';
    this.castle = true;
    this.color = color;
}

king.prototype = Object.create(Piece.prototype);

king.prototype.isCastle = function() {
    return this.castle;
};

king.prototype.moved = function() {
    this.castle = false;
};

module.exports = king;