var Piece = require('./Piece');

// Now, this one will call the "superclass"'s constructor and apply it to itself
function pawn(color) {
    Piece.call(this);
    this.name = 'P';
    this.firstMove = true;
    this.color = color;
}

pawn.prototype = Object.create(Piece.prototype);

pawn.prototype.isFirstMove = function() {
    return this.firstMove;
};

pawn.prototype.moved = function() {
    this.firstMove = false;
};

module.exports = pawn;