var bus = require('../systemBus');
var board = require('board');

function gameEngine() {

    bus.on('move', function(data) {
        bus.emit('moved', data);
    });
    bus.on('clear', function() {
        // Destroy the game
    });

    this.board = new board();
    return this.board.getPiecemap();
}

module.exports = gameEngine;