// Observer index.js

var isMoveLegal = require('legalChecker');

var observer = function(board, pieces) {
    var that = this;
    this.board = board;
    this.pieces = pieces;

    this.moveCheck = function(square, file, rank) {
        return isMoveLegal(square, file, rank, that);
    };
};

// Check if the move is legal for the given piece
observer.prototype.move = function(square, file, rank) {
    return this.moveCheck(square, file, rank);
};

module.exports = observer;
