var Piece = require('./Piece');

// Now, this one will call the "superclass"'s constructor and apply it to itself
function bishop(color, id, square) {
    Piece.call(this);
    this.name = 'B';
    this.color = color;
    this.id = id;

    this.hack = square;
}

bishop.prototype = Object.create(Piece.prototype);

module.exports = bishop;