var Piece = require('./Piece');

// Now, this one will call the "superclass"'s constructor and apply it to itself
function empty(color) {
    Piece.call(this);
    this.name = ' ';
    this.color = -1;
}

empty.prototype = Object.create(Piece.prototype);

empty.prototype.isMoved = function() {
    console.log("Don't call ismoved from an empty piece");
    return false;
};

empty.prototype.moved = function() {
    console.log("Don't call moved from an empty piece");
};

module.exports = empty;