var Piece = require('./Piece');

// Now, this one will call the "superclass"'s constructor and apply it to itself
function king(color, id, square) {
    Piece.call(this);
    this.name = 'K';
    this.isNotMoved = true;
    this.color = color;
    this.id = id;

    // Hack to make checking for mate easier
    this.hack = square;
}

king.prototype = Object.create(Piece.prototype);

king.prototype.isMoved = function() {
    return this.isNotMoved;
};

king.prototype.moved = function() {
    this.isNotMoved = false;
};
module.exports = king;