var Piece = require('./Piece');

// Now, this one will call the "superclass"'s constructor and apply it to itself
function king(color) {
    Piece.call(this);
    this.name = 'K';
    this.isMoved = true;
    this.color = color;
    this.isChecked = false;
}

king.prototype = Object.create(Piece.prototype);

king.prototype.isMoved = function() {
    return this.isMoved;
};

king.prototype.moved = function() {
    this.isMoved = false;
};

king.prototype.isChecked = function() {
    this.isChecked = true;
}

king.prototype.isUnchecked = function() {
    this.isChecked = false;
}
module.exports = king;