var Piece = require('./Piece');

// Now, this one will call the "superclass"'s constructor and apply it to itself
function rook(color, id) {
    Piece.call(this);
    this.name = 'R';
    this.isNotMoved = true;
    this.color = color;
    this.id = id;
}

rook.prototype = Object.create(Piece.prototype);

rook.prototype.isMoved = function() {
    return !(this.isNotMoved);
};

rook.prototype.moved = function() {
    this.isMoved = false;
};

module.exports = rook;